
function sub(){
	var name = $('input[name=user_name]').val();
	var pass = $('input[name=user_passw]').val();
	var code = $('input[name=code]').val();
	if(name=="" || name ==null){
		$('#errormessage').text("请输入用户名");return false;
	}else if(pass=="" || pass ==null){
		$('#errormessage').text("请输入密码");return false;
	}else if(code=="" || code ==null || code=="验证码"){
		$('#errormessage').text("请输入验证码");return false;
	}else{
		return true;
	}
};

var i = 0;
function alltext(alltext,text) {
	var length = $('#'+alltext).val().length;
	var str = $('#'+alltext).val();
	if(i<length){
		$('#'+text).html($('#'+text).text()+str.charAt(i));
		i++;
	}else{
		$('#'+text).html("&nbsp;&nbsp;");
		i=0;
	}
} 







